import React, { useState } from "react";

import "./Items.css";
import ExpensesList from "./ItemsList";
import ExpensesFilter from "./ItemsFilter";
import ExpensesChart from './ItemsChart'
import Card from "../UI/Card";

const Expenses = (props) => {
  const [filterYear, setFilterYear] = useState("All");

  const filterDateChangeHandler = (selectedDate) => {
    setFilterYear(selectedDate);
  };

  const filteredExpenses = props.itemsList.filter((each) => {
    if (filterYear !== "All") {
      return each.date.getFullYear().toString() === filterYear;
    } else {
      return each;
    }
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filterYear}
          onChangeFilterDate={filterDateChangeHandler}
        />
        <ExpensesChart expenses={filteredExpenses}/>
        <ExpensesList itemsRendering={filteredExpenses}/>
      </Card>
    </div>
  );
};

export default Items;
