import React, { useState } from "react";

import NewItem from "./components/NewItem/NewItem";
import Items from "./components/Items/Items";


const dumyList = [
  {
    id: "1",
    title: "React Reviews",
    duration: 1,
    date: new Date(2022, 6, 14),
  },
  {
    id: "1",
    title: "React Hooks",
    duration: 2,
    date: new Date(2022, 7, 14),
  },
  {
    id: "3",
    title: "React States",
    duration: 1,
    date: new Date(2022, 8, 14),
  },
  { id: "4", title: "Ski Holiday ", duration: 72, date: new Date(2021, 2, 12) },
  {
    id: "5",
    title: "Home Renovation",
    duration: 7,
    date: new Date(2022, 2, 28),
  },
  {
    id: "6",
    title: "Family Visit",
    duration: 2,
    date: new Date(2022, 5, 12),
  }
];


const App = () => {
  const [toDoList, setTodoList] = useState(dumyList);

  const addItemsHandler = (newItem) => {
    setTodoList((prevItems) => {
      return [newItem, ...prevItems];
    });
  };


  return (
    <div>
      <NewItem onAddItem={addItemsHandler} />
      <Items itemsList={toDoList} />
    </div>
  );
};

export default App;
