import React, { useState } from "react";

import "./Items.css";
import ExpensesList from "./ItemsList";
import ExpensesFilter from "./ItemsFilter";
import ExpensesChart from './ItemsChart'
import Card from "../UI/Card";

const Items = (props) => {

  const [filterMounth, setFilterMounth] = useState("All");

  const filterDateChangeHandler = (selectedMounth) => {
    setFilterMounth(selectedMounth);
  };

  const filteredExpenses = props.itemsList.filter((each) => {
    if (filterMounth !== "All") {
      return each.date.getFullYear().toString() === filterMounth;
    } else {
      return each;
    }
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filterMounth}
          onChangeFilterMounth={filterMounthChangeHandler}
        />
        <ExpensesChart expenses={filteredExpenses}/>
        <ExpensesList itemsRendering={filteredExpenses}/>
      </Card>
    </div>
  );
};

export default Items;
